import React, { useState } from "react";
import Papa from "papaparse";
import Select from "react-select";
import DatePicker from "react-datepicker"; // Import the date picker
import "react-datepicker/dist/react-datepicker.css"; // Import date picker styles
import './App.css'; // Import your CSS file

function App() {
  const [currencyData, setCurrencyData] = useState([]);
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedCurrency, setSelectedCurrency] = useState(null);
  const [conversionRate, setConversionRate] = useState(null);
  const [convertedRates, setConvertedRates] = useState([]);
  const [sortOption, setSortOption] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      Papa.parse(file, {
        header: true,
        complete: (results) => {
          const data = results.data;

          // Parse dates and ensure they're valid Date objects
          const uniqueDates = [...new Set(data.map(row => {
            const date = new Date(row.Date);
            return isNaN(date.getTime()) ? null : date; // Ensure valid date
          }))].filter(date => date !== null); // Filter out invalid dates

          setDates(uniqueDates);
          setCurrencyData(data);
        }
      });
    }
  };

  const convertRates = (baseCurrency) => {
    if (!selectedDate) return;

    const formattedDate = selectedDate.toISOString().split('T')[0]; // Format date as YYYY-MM-DD
    const selectedRow = currencyData.find(row => row.Date === formattedDate);

    if (selectedRow) {
      const baseRate = parseFloat(selectedRow[baseCurrency]);
      setConversionRate(baseRate);

      const newRates = Object.keys(selectedRow)
        .filter(key => key !== 'Date' && key !== baseCurrency)
        .map(currency => {
          const convertedRate = (parseFloat(selectedRow[currency]) / baseRate).toFixed(4);
          return {
            Currency: currency,
            ConvertedRate: convertedRate,
          };
        });

      // Include the base currency with a conversion rate of 1
      newRates.push({
        Currency: baseCurrency,
        ConvertedRate: "1.0000",
      });

      setConvertedRates(newRates);
    } else {
      console.log("No data found for the selected date.");
    }
  };

  const sortOptions = [
    { value: 'name', label: 'Name of Currency' },
    { value: 'rate', label: 'Conversion Rate Value' }
  ];

  const handleSortChange = (option) => {
    setSortOption(option);
    if (convertedRates.length > 0) {
      let sortedRates = [...convertedRates];
      if (option.value === 'name') {
        sortedRates.sort((a, b) => a.Currency.localeCompare(b.Currency));
      } else if (option.value === 'rate') {
        sortedRates.sort((a, b) => parseFloat(a.ConvertedRate) - parseFloat(b.ConvertedRate));
      }
      setConvertedRates(sortedRates);
    }
  };

  const currencyOptions = currencyData.length > 0
    ? Object.keys(currencyData[0]).filter(key => key !== 'Date').map(currency => ({
        value: currency,
        label: currency,
      }))
    : [];

  return (
    <div className="App">
      <h1>Currency Converter</h1>

      <input type="file" accept=".csv" onChange={handleFileUpload} />

      {/* Date Picker */}
      {dates.length > 0 && (
        <DatePicker
          selected={selectedDate}
          onChange={(date) => {
            setSelectedDate(date);
            convertRates(selectedCurrency); // Reconvert rates on date change
          }}
          dateFormat="yyyy/MM/dd"
          placeholderText="Select Date"
          isClearable
          showYearDropdown
          yearDropdownItemNumber={15}
          scrollableYearDropdown
          filterDate={(date) => {
            // Ensure the date is valid and check if it exists in the unique dates
            return dates.some(d => d.getTime() === date.getTime());
          }}
        />
      )}

      {selectedDate && currencyOptions.length > 0 && (
        <Select
          options={currencyOptions}
          onChange={(option) => {
            setSelectedCurrency(option.value);
            convertRates(option.value);
          }}
          placeholder="Select Base Currency"
        />
      )}

      {selectedCurrency && conversionRate && (
        <div className="conversion-rate">
          <h2>
            Conversion Rate for {selectedCurrency} (Base Currency): {conversionRate}
          </h2>
        </div>
      )}

      {convertedRates.length > 0 && (
        <div className="converted-rates">
          <h3>Converted Rates (relative to {selectedCurrency}):</h3>

          {/* Sorting Dropdown */}
          <Select
            options={sortOptions}
            onChange={handleSortChange}
            placeholder="Sort By"
          />

          <table>
            <thead>
              <tr>
                <th>Currency</th>
                <th>Converted Rate</th>
              </tr>
            </thead>
            <tbody>
              {convertedRates.map((currency, index) => (
                <tr key={index}>
                  <td>{currency.Currency}</td>
                  <td>{currency.ConvertedRate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
