import React, { useEffect } from 'react';
import { Chart } from 'chart.js';

const CurrencyChart = () => {
  useEffect(() => {
    const ctx = document.getElementById('exchangeRateChart').getContext('2d');

    // Example data; replace this with actual data fetch
    const exampleData = {
      labels: ['2024-01-01', '2024-01-02', '2024-01-03', '2024-01-04', '2024-01-05'],
      datasets: [{
        label: 'Exchange Rate (USD)',
        data: [1.1, 1.2, 1.15, 1.3, 1.25],
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 2,
        fill: false,
      }]
    };

    new Chart(ctx, {
      type: 'line',
      data: exampleData,
      options: {
        responsive: true,
        scales: {
          x: {
            title: {
              display: true,
              text: 'Date'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Exchange Rate'
            }
          }
        }
      }
    });
  }, []);

  return (
    <div>
      <h1>Exchange Rate Graph</h1>
      <canvas id="exchangeRateChart" width="600" height="400"></canvas>
    </div>
  );
};

export default CurrencyChart;
