import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import HomePage from './HomePage';
import CurrencyChart from './CurrencyChart';
import CustomCurrencyBasket from './CustomCurrencyBasket'; // Import your other components
import FXRates from './FXRates'; // Import the FXRates component

const App = () => {
  return (
    <Router>
      <div className="App">
        <nav>
          <ul className="navbar">
            <li>
              <Link to="/">Currency Chart</Link>
            </li>
            <li>
              <Link to="/custom-basket">Custom Currency Basket</Link>
            </li>
            <li>
              <Link to="/fx-rates">FX Rates for All Currencies</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<CurrencyChart />} />
          <Route path="/custom-basket" element={<CustomCurrencyBasket />} />
          <Route path="/fx-rates" element={<FXRates />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
