import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './HomePage';
import CurrencyChart from './CurrencyChart';  // Make sure this path is correct
import CustomCurrencyBasket from './CustomCurrencyBasket'; // Ensure this path is correct
import FXRates from './FXRates'; // Ensure this path is correct
import './App.css';

const App = () => {
  return (
    <Router>
      <div className="App">
        <h1>XChange</h1>
        <nav>
          <Link to="/">Home</Link>
          <Link to="/currency-chart">Currency Chart</Link>
          <Link to="/custom-currency-basket">Custom Currency Basket</Link>
          <Link to="/fx-rates">FX Rates for All Currencies</Link>
        </nav>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/currency-chart" element={<CurrencyChart />} />
          <Route path="/custom-currency-basket" element={<CustomCurrencyBasket />} />
          <Route path="/fx-rates" element={<FXRates />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
