import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const App = () => {
  const [currencyData, setCurrencyData] = useState([]);
  const [convertedRates, setConvertedRates] = useState([]);
  const [selectedCurrency, setSelectedCurrency] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [conversionRate, setConversionRate] = useState(0);

  // Function to fetch currency data (mockup for demonstration)
  const fetchCurrencyData = () => {
    // Replace this with your actual data fetching logic
    const data = [
      { Date: "2024-09-24", DZD: 1, AUD: 0.0100, BHD: 0.0028, VEF: 0.0461, COP: 28.1196 },
      { Date: "2024-09-25", DZD: 1, AUD: 0.0101, BHD: 0.0027, VEF: 0.0460, COP: 28.2000 },
      // Add more rows as needed
    ];
    setCurrencyData(data);
  };

  useEffect(() => {
    fetchCurrencyData();
  }, []);

  const convertRates = (baseCurrency) => {
    if (!selectedDate) return;

    const formattedDate = selectedDate.toISOString().split('T')[0];
    const selectedRow = currencyData.find(row => row.Date === formattedDate);

    if (selectedRow) {
      const baseRate = parseFloat(selectedRow[baseCurrency]);

      // Ensure baseRate is a valid number
      if (isNaN(baseRate)) {
        console.error("Invalid base rate for selected currency:", baseCurrency);
        return;
      }

      setConversionRate(baseRate);

      // Process the conversion rates
      const newRates = Object.keys(selectedRow)
        .filter(key => key !== 'Date' && selectedRow[key] !== "") // Exclude empty and Date column
        .map(currency => {
          const rate = parseFloat(selectedRow[currency]);
          return {
            Currency: currency,
            ConvertedRate: isNaN(rate) ? "N/A" : (rate / baseRate).toFixed(4) // Handle invalid rates
          };
        })
        .filter(rate => rate.ConvertedRate !== "N/A"); // Remove any rates that couldn't be calculated

      // Add the base currency with a conversion rate of 1
      newRates.push({ Currency: baseCurrency, ConvertedRate: "1.0000" });

      // Remove duplicates
      const uniqueRates = Array.from(new Map(newRates.map(rate => [rate.Currency, rate])).values());
      setConvertedRates(uniqueRates);
    }
  };

  const handleCurrencyChange = (option) => {
    setSelectedCurrency(option.value);
    convertRates(option.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    if (selectedCurrency) {
      convertRates(selectedCurrency);
    }
  };

  const handleSortChange = (option) => {
    const sortedRates = [...convertedRates].sort((a, b) => {
      return option.value === 'name' 
        ? a.Currency.localeCompare(b.Currency) 
        : parseFloat(a.ConvertedRate) - parseFloat(b.ConvertedRate);
    });
    setConvertedRates(sortedRates);
  };

  const currencyOptions = [
    { value: 'DZD', label: 'Algerian Dinar (DZD)' },
    { value: 'AUD', label: 'Australian Dollar (AUD)' },
    { value: 'BHD', label: 'Bahrain Dinar (BHD)' },
    { value: 'VEF', label: 'Bolivar Fuerte (VEF)' },
    { value: 'COP', label: 'Colombian Peso (COP)' },
    // Add more currency options as needed
  ];

  return (
    <div className="app">
      <h1>Currency Converter</h1>

      <Select
        options={currencyOptions}
        onChange={handleCurrencyChange}
        placeholder="Select a base currency"
      />

      <DatePicker
        selected={selectedDate}
        onChange={handleDateChange}
        dateFormat="yyyy/MM/dd"
        isClearable
      />

      {convertedRates.length > 0 && (
        <div className="converted-rates">
          <h3>Converted Rates (relative to {selectedCurrency}):</h3>
          <Select
            options={[
              { value: 'name', label: 'Name of Currency' },
              { value: 'rate', label: 'Conversion Rate Value' }
            ]}
            onChange={handleSortChange}
            placeholder="Sort By"
          />
          <table>
            <thead>
              <tr>
                <th>Currency</th>
                <th>Converted Rate</th>
              </tr>
            </thead>
            <tbody>
              {convertedRates.map((currency, index) => (
                <tr key={index}>
                  <td>{currency.Currency}</td>
                  <td>{currency.ConvertedRate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default App;
