import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import HomePage from './HomePage'; // Your homepage component
import FXRates from './FXRates'; // The FX Rates component
import './App.css'; // Your main CSS file for styling

function App() {
  const [currencyData, setCurrencyData] = useState([]); // State for currency data
  const [dates, setDates] = useState([]); // State for unique dates from the CSV

  return (
    <Router>
      <div className="App">
        <h1>XChange</h1>
        <nav>
          {/* Navigation Links */}
          <Link to="/fx-rates">FX Rates for All Currencies</Link>
          <Link to="/custom-basket">Custom Currency Basket</Link>
          <Link to="/risk-indicator">Risk Indicator</Link>
          <Link to="/currency-information">Currency Information</Link>
        </nav>

        <Routes>
          {/* Route for Home Page */}
          <Route path="/" element={<HomePage />} />
          {/* Route for FX Rates Page with state passed down */}
          <Route 
            path="/fx-rates" 
            element={
              <FXRates 
                currencyData={currencyData} 
                setCurrencyData={setCurrencyData} 
                dates={dates} 
                setDates={setDates} 
              />
            } 
          />
          {/* Add additional routes here for other components */}
          {/* <Route path="/custom-basket" element={<CustomBasket />} /> */}
          {/* <Route path="/risk-indicator" element={<RiskIndicator />} /> */}
          {/* <Route path="/currency-information" element={<CurrencyInformation />} /> */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
