import React, { useState } from "react";
import Papa from "papaparse";
import Select from "react-select";

function App() {
  const [currencyData, setCurrencyData] = useState([]);
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedCurrency, setSelectedCurrency] = useState(null);
  const [conversionRate, setConversionRate] = useState(null); // Store base conversion rate
  const [convertedRates, setConvertedRates] = useState([]);

  // Handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      Papa.parse(file, {
        header: true,
        complete: (results) => {
          const data = results.data;

          // Get unique dates
          const uniqueDates = [...new Set(data.map(row => row.Date))];
          setDates(uniqueDates);
          setCurrencyData(data);
        }
      });
    }
  };

  // Convert rates based on selected date and base currency
  const convertRates = (baseCurrency) => {
    if (!selectedDate) return;

    const selectedRow = currencyData.find(row => row.Date === selectedDate);

    if (selectedRow) {
      const baseRate = parseFloat(selectedRow[baseCurrency]);
      setConversionRate(baseRate); // Set the base currency's conversion rate

      const newRates = Object.keys(selectedRow)
        .filter(key => key !== 'Date' && key !== baseCurrency) // Filter out the base currency itself
        .map(currency => ({
          Currency: currency,
          ConvertedRate: (parseFloat(selectedRow[currency]) / baseRate).toFixed(4),
        }));

      setConvertedRates(newRates);
    }
  };

  const dateOptions = dates.map(date => ({ value: date, label: date }));
  const currencyOptions = currencyData.length > 0
    ? Object.keys(currencyData[0]).filter(key => key !== 'Date').map(currency => ({
        value: currency,
        label: currency,
      }))
    : [];

  return (
    <div className="App">
      <h1>Currency Converter</h1>

      {/* Upload CSV file */}
      <input type="file" accept=".csv" onChange={handleFileUpload} />

      {/* Date Select Dropdown */}
      {dates.length > 0 && (
        <Select
          options={dateOptions}
          onChange={(option) => setSelectedDate(option.value)}
          placeholder="Select Date"
        />
      )}

      {/* Currency Select Dropdown */}
      {currencyData.length > 0 && selectedDate && (
        <Select
          options={currencyOptions}
          onChange={(option) => {
            setSelectedCurrency(option.value);
            convertRates(option.value);
          }}
          placeholder="Select Base Currency"
        />
      )}

      {/* Display the base currency conversion rate */}
      {selectedCurrency && conversionRate && (
        <div>
          <h2>
            Conversion Rate for {selectedCurrency} (Base Currency): {conversionRate}
          </h2>
        </div>
      )}

      {/* Display other converted rates */}
      {convertedRates.length > 0 && (
        <div>
          <h3>Converted Rates (relative to {selectedCurrency}):</h3>
          <table>
            <thead>
              <tr>
                <th>Currency</th>
                <th>Converted Rate</th>
              </tr>
            </thead>
            <tbody>
              {convertedRates.map((currency, index) => (
                <tr key={index}>
                  <td>{currency.Currency}</td>
                  <td>{currency.ConvertedRate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
